import { useState, useEffect } from 'react'
const useFetch = (busca) => {
    const [data, setData] = useState('')
    useEffect(() => {
    fetch(`http://localhost:3000/articles?search=${busca}`)
        .then(v => v.json())
        .then(v => setData(v))
},[busca])
    return data
}

function Buscar({setSearch}) {
    const [busca, setBusca] = useState('')

    const data = useFetch(busca)
    setSearch(data)

    const handleSubmit = e => {
        e.preventDefault()
        fetch(`http://localhost:3000/articles?search=${busca}`)
        .then(v => v.json())
        .then(v => {
            setSearch(v)
            console.log(v)
        })
    }

    return (
        <form onSubmit={handleSubmit}>
            <label>
                <input value={busca} name="username" onChange={(e)=>setBusca(e.target.value)} placeholder='ðŸ” Buscar'/>
            </label>
            <button>Encontar</button>
        </form>
    )
}

export default Buscar
