import { useState, useEffect } from 'react'
import { useParams } from "react-router-dom"

const useGet = (id) => {
    const [data, setData] = useState('')
    useEffect(() => {
    fetch(`http://localhost:3000/articles/${id}`)
        .then(v => {
            let data
            if (v.ok) data = v.json()
           return data
        })
        .then(v => setData(v))
    },[id])
    return data
}
const muestra = [
    {
        "id": Math.random(),
        "name": "Producto no encontrado",
        "price": "",
        "description": "Prueba con otro nombre",
        "photo": "https://previews.123rf.com/images/trueffelpix/trueffelpix1605/trueffelpix160500018/58037157-vector-ilustraci%C3%B3n-de-la-p%C3%A1gina-de-error-no-encontrado.jpg",
        "idUser": Math.random,
        "buyerId": null,
        "createdAt": "2022-01-04T23:00:00.000Z",
        "modifiedAt": null
    }
  ]

function PaginaArticuloId() {
    const [datos, setDatos] = useState(muestra)
    const { id } = useParams()
    const product = useGet(id)
    console.log(product)
    window.addEventListener("load",setDatos(useGet(id)))

    return (
        datos ?
            <div className='reloj-loading'></div>
            :
            datos?.map(product =>
            <div key={product.id} className='product'>
                <img src={product.photo} alt={product.name} className='photo'/>
                <div className='content'>
                    <h1>{product.name}</h1>
                    <div className='description'>
                        <p>{product.description}</p> <span>{product.price}</span>
                    </div>
                </div>
            </div>)
    )
}

export default PaginaArticuloId
