import { useState, useEffect } from 'react'
import { useParams } from "react-router-dom"
import Logo from './Logo'
import AsideArticle from './AsideArticle'
import './ArticlesPage.css'
import { useUser } from '../Container/UserContext'

const useGet = ({id}) => {
    const [data, setData] = useState('')
    useEffect(() => {
    fetch('http://localhost:3000/articles/' + id)
        .then(v => {
            let data
            if (v.ok) data = v.json()
           return data
        })
        .then(v => setData(v))
    },[id])
    return data
}
const useName = ({id}) => {
    const [data, setData] = useState('')
    useEffect(() => {
    fetch('http://localhost:3000/users/name/' + id)
        .then(v => {
            let data
            if (v.ok) data = v.json()
           return data
        })
        .then(v => setData(v))
    },[id])
    return data
}

function PaginaArticuloId() {
    const [data, setData] = useState(null)
    const [name, setName] = useState(null)
    const { id } = useParams()
    const product = useGet({id})
    const username = useName({id: product?.idUser})
    useEffect(() => {
        if (product) setData(product)
        if (username) setName(username)
    },[product, username])

    return (
        <div className="ArticlePage">
        <header className="headerArticle">
         <Logo />
        </header>
        <aside className='asideArticle'>
          <AsideArticle />
        </aside>
        <main className='mainArticle'>
            { (!data || !name) ?
            <div className='reloj-loading'></div>
            :
            [data]?.map(article =>
            <div key={article.id} className='productArticle'>
                <div className='containerArticle'>
                    <img src={`http://localhost:3000${article.photo.replace('./', '/')}`} alt={article.name} className='photo'/>
                    <div className="puntuacion">
                        <h1>{name[0].username}</h1><span>{'★★★★★☆☆☆☆☆'.substring(5 - article.userAverageVotes, 10 - article.userAverageVotes)}</span>
                    </div>

                </div>
                <div className='contentArticle'>
                    <h1>{article.name}</h1>
                    <div className='descriptionArticle'>
                        <p>{article.description}</p>
                        <div className='spanButtonArticle'>
                            <span> Precio: {article.price}€</span>
                            <button type='button' className='irCompraArticle'><a href={'http://localhost:3001/article/' + article.id} className='enlaceArticle'>ir a comprar</a></button>
                        </div>
                    </div>
                </div>
            </div>)
            }
        </main>
        <footer className='footerArticle'>

        </footer>
      </div>

    )
}

export default PaginaArticuloId
