import { useEffect, useState } from 'react'
import './Session.css'
import Modal from './Modal'
import { Navigate, NavLink } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'


const perfil = async ({token}) => {
  const res = await   fetch(`http://localhost:3000/users/profile`,{
    headers: new Headers({
      "Authorization" : `Bearer ${token}`
    })
  })
  let data = await res.json()

  if (res.ok) {
    return data
  } else {
    return data.error
  }
}

function Session({show, setShow}) {
  const dispatch = useDispatch()
    const user = useSelector(s => s.user)
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [error, setError] = useState(null)
    const [loading, setLoading] = useState(false)
    const [usuario, setUsuario] = useState(null)
    const [datosUsuario, setDatosUsuario] = useState(null)

    const handleSubmit = async e => {
      setLoading(true)
      e.preventDefault()
      const res = await fetch('http://localhost:3000/users/login', {
        method: 'POST',
        body: JSON.stringify({email, password}),
        headers: new Headers({
          'Content-Type': 'application/json'
        })
      })
      // let data = await res.json()

      if (res.ok) {
        let data = await res.json()
        setLoading(false)
        dispatch({ type: 'login', user: data })
        setShow(false)
        // setUsuario(data)
        const datos = await perfil(data)
        setDatosUsuario(datos)
      } else {
        setError({status: res.status, statusText:res.statusText})
        setLoading(false)
      }
    }
    useEffect(() => {
      if (user)
      setUsuario(true)
    },[user])
    return (
      usuario ?
        (datosUsuario &&
        <div className='perfil'>
          <img className='avatar' src={`http://localhost:3000${datosUsuario[0].avatar.replace('./', '/')}`} alt={datosUsuario[0].username}></img>
          {datosUsuario[0].username}
        </div>)
        :
        <div>
            <button onClick={ () => setShow(!show)}>Iniciar sesión</button>
            <Modal show={show} setShow={setShow}>
            {loading ?
                  <div className='reloj-loading'></div>
                :
                <fieldset>
                    <legend>Login</legend>
                    <form onSubmit={handleSubmit} className='login'>
                        <label>
                        Usuario:
                        <input required name="email" value={email} onChange={e => setEmail(e.target.value)} />
                        </label>
                        <label>
                        Contraseña:
                        <input required type="password" name="password" value={password}  onChange={e => setPassword(e.target.value)} />
                        </label>
                        <button>Entrar</button>
                    </form>
                    {error && <div className="error">{error.status}{error.statusText}</div>}
                </fieldset>}
                <button>
                  <NavLink to='../user/registre'>No estoy registrado</NavLink>
                </button>
            </Modal>
        </div>
    )
  }

  export default Session
