function Search({setSearch}) {
    const [search, setSearchFor] = useState('')

    const data = useFetch(`http://localhost:3000/articles?search=${search}`)
    setSearch(data)

    const handleSubmit = e => {
        e.preventDefault()
    }

    return (
        <form onSubmit={handleSubmit}>
            <label>
                <input value={search} name="username" onChange={(e)=>setSearchFor(e.target.value)} placeholder='🔍 Buscar'/>
            </label>
            <button>Encontar</button>
        </form>
    )
}

const PersonajeWrapper = ({setSearch}) =>
  <Suspense fallback={<Loading className="page" />}>
    <Search setSearch={setSearch}/>
  </Suspense>


export default PersonajeWrapper
