import React, { useState } from 'react'


function Registre({show, setShow, setUser}) {
    const [email, setEmail] = useState('')
    const [name, setName] = useState('')
    const [password, setPassword] = useState('')
    const [ubicacion, setUbicacion] = useState('')
    const [avatar, setAvatar] = useState('')
    const [loading, setLoading] = useState(false)

    const api = 'http://localhost:3000/users/'

    const avatarURL= avatar && URL.createObjectURL(avatar)

    const user = {}
      user.username = name
      user.password = password
      user.email = email
      user.location = ubicacion

    console.log(avatarURL)
    const handleSubmit = async e => {
        setLoading(true)
      e.preventDefault()

      if (avatar && name && password && ubicacion) {
        const fd = new FormData()
        fd.append('avatar', avatar)
        fd.append('user', JSON.stringify(user))
        const res = await fetch(api, {
          method: 'POST',
          body: fd
        })
        const data = await res.json()
        if (res.ok) {
          // dispatch({ type: 'login', user: data })
          // navigate('/perfil')
          console.log(data)
          setLoading(!true)
        } else {
          console.log(data.error)
          setLoading(!true)
        }


      setEmail('')
      setName('')
      setPassword('')
      setUbicacion('')
    }
  }
    // const handleFile = e => {
    //     const f = e.target.files[0]
        // if (f) {
        //   const fd = new FormData()
        //   fd.append('image', f)
        //   fd.append('name', 'demo')
        //   fetch(api + '/image', {
        //     method: 'POST',
        //     body: fd
        //   })
    //         .catch(e => console.error(e))
    //     }
    //   }
    return (
            loading ?
                <div className='reloj-loading'>
                    <button onClick={() => setLoading(false)}>otra vez</button>
                </div> :
                <fieldset>
                    <legend>Registrarse</legend>
                    <form onSubmit={handleSubmit} className='login'>
                        <label>
                        Email:
                        <br/>
                        <input name="email" value={email} onChange={e => setEmail(e.target.value)} />
                        </label>
                        <label>
                        Nombre:
                        <br/>
                        <input name="email" value={name} onChange={e => setName(e.target.value)} />
                        </label>
                        <label>
                        Contraseña:
                        <br/>
                        <input type="password" name="password" value={password}  onChange={e => setPassword(e.target.value)} />
                        </label>
                        <label>
                        Ubicacion:
                        <br/>
                        <input name="ubicacion" value={ubicacion}  onChange={e => setUbicacion(e.target.value)} />
                        </label>
                        <label>
                          {!avatarURL ?
                          <div>
                            <div className="button">📷</div>
                            <input type="file" onChange={e => setAvatar(e.target.files[0])} accept="image/x-png,image/gif,image/jpeg" />
                          </div>
                          :
                          <div className='avatarDiv'>
                            <img className='avatar avatarRegistre' src={avatarURL} alt='avatar'/>
                            <input type="file" onChange={e => setAvatar(e.target.files[0])} accept="image/x-png,image/gif,image/jpeg" />
                          </div>}
                        </label>
                          <div className='enviarRegistro'>
                            <button>Enviar</button>
                        </div>

                    </form>
                </fieldset>
    )
  }

  export default Registre
