import { useState } from 'react'
import './Session.css'
import Modal from './Modal'
import { useUser } from '../container/UserContext'


const perfil = async ({token}) => {
  const res = await   fetch(`http://localhost:3000/users/profile`,{
    headers: new Headers({
      "Authorization" : `Bearer ${token}`
    })
  })
  let data = await res.json()

  if (res.ok) {
    return data
  } else {
    return data.error
  }
}

function Session({show, setShow}) {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [error, setError] = useState(null)
    const [loading, setLoading] = useState(false)
    const [usuario, setUsuario] = useState(null)
    const [datosUsuario, setDatosUsuario] = useState(null)
    const [, setUser] = useUser()

    const handleSubmit = async e => {
        setLoading(true)
      e.preventDefault()
      const res = await fetch('http://localhost:3000/users/login', {
        method: 'POST',
        body: JSON.stringify({email, password}),
        headers: new Headers({
          'Content-Type': 'application/json'
        })
      })
      let data = await res.json()

      if (res.ok) {
        setLoading(false)
        setUser(data)
        setShow(false)
        setUsuario(data)
        const datos = await perfil(data)
        setDatosUsuario(datos)
      } else {
        setLoading(false)
        setError(data.error)
      }
    }
    return (
        usuario ?
        (datosUsuario &&
        <div className='perfil'>
          <img className='avatar' src={`http://localhost:3000${datosUsuario[0].avatar.replace('./', '/')}`} alt={datosUsuario[0].username}></img>
          {datosUsuario[0].username}
        </div>)
        :
        <div>

            {loading ? <div className='reloj-loading'></div> :
                <fieldset>
                    <legend>Login</legend>
                    <form onSubmit={handleSubmit} className='login'>
                        <label>
                        Usuario:
                        <input name="email" value={email} onChange={e => setEmail(e.target.value)} />
                        </label>
                        <label>
                        Contraseña:
                        <input type="password" name="password" value={password}  onChange={e => setPassword(e.target.value)} />
                        </label>
                        <button>Entrar</button>
                        {error && <div className="error">{error}</div>}
                    </form>
                </fieldset>}
        </div>
    )
  }

  export default Session
