import React from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter} from 'react-router-dom'
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import rootReducer from './reducer'
import { createStore } from 'redux'
import { Provider } from 'react-redux'
import { applyMiddleware } from 'redux'

// const store = createStore(rootReducer)
const localStorageMiddleware = store => next => action => {
  let result = next(action)
  localStorage.setItem('session', JSON.stringify(store.getState().user))
  localStorage.setItem('datos', JSON.stringify(store.getState().userDatos))
  return result
}

const saved = localStorage.getItem('session')
const saved2 = localStorage.getItem('datos')
const initialStore = { user: saved ? JSON.parse(saved) : undefined }
const initialStore2 = { user: saved2 ? JSON.parse(saved2) : undefined }
const store = createStore(rootReducer, initialStore, initialStore2, applyMiddleware(localStorageMiddleware))

ReactDOM.render(
  <React.StrictMode>
    <BrowserRouter>
      <Provider store={store}>
        <App />
      </Provider>
    </BrowserRouter>
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
