import { useState } from 'react'


function Products({products}) {
    const [step, setStep] = useState(0)
    const perPage = 20
    const pags = Math.ceil(products.length / perPage)
    const handlePrev = () => setStep(step > 0 ? step - 1 : pags - 1)
    const handleNext = () => setStep((step + 1) % pags)
    console.log(products[0].photo.replace('./', '/'))
    return (
      <div>
        {products.slice(step * perPage, (step + 1) * perPage).map(product =>
        <div key={product.id} className='producto'>
            <img src={`http://localhost:3000${product.photo.replace('./', '/')}`} alt={product.name}/>
            <h1>{product.name}</h1>
            <p>{product.description}</p> <span>{product.price}</span>
        </div>
        )}
        <button onClick={handlePrev}>
          Anterior
          <span />
        </button>
        <button onClick={handleNext}>
          Siguiente
        </button>
      </div>
    )
}

export default Products
