import { useState, useEffect } from 'react'

function Buscar({setSearch}) {
    const [busca, setBusca] = useState('')
    const handleSubmit = e => {
        e.preventDefault()
        // setSearch(busca)
    }
    useEffect(() => {
        fetch(`http://localhost:3000/articles?search=${busca}`)
            .then(v => v.json())
            .then(v => setSearch(v))
    },[])

    return (
        <form onSubmit={handleSubmit}>
            <label>
                <input value={busca} name="username" onChange={(e)=>setBusca(e.target.value)} placeholder='🔍 Buscar'/>
            </label>
            <button>Encontar</button>
        </form>
    )
}

export default Buscar
